import Header from "./component/layout/Header/Header.js"
import './App.css';
import { BrowserRouter , Routes,Route } from "react-router-dom";
import WebFont from "webfontloader"
import React, { Fragment, useState } from "react";
import Footer from "./component/layout/Footer/Footer.js"
import Home from "./component/Home/Home.js"
import ProductDetails from "./component/Product/ProductDetails.js"
import Products from "./component/Product/Products.js";
import Search from "./component/Product/Search.js";
import LoginSignUp from "./component/User/LoginSignUp.js"
import store from "./store.js";
import { loadUser } from "./actions/userAction.js";
import UserOptions from "./component/layout/Header/UserOptions.js"
import { useSelector } from "react-redux";
import Profile from "./component/User/Profile.js"
// import ProtectedRoute from "./component/Route/ProtectedRoute.js";
import UpdateProfile from "./component/User/UpdateProfile.js"
import UpdatePassword from "./component/User/UpdatePassword.js";
import ForgotPassword from "./component/User/ForgotPassword.js";
import ResetPassword from "./component/User/ResetPassword.js";
import Cart from "./component/Cart/Cart.js";
import Shipping from "./component/Cart/Shipping.js"
import ConfirmOrder from "./component/Cart/ConfirmOrder.js";
import axios from "axios";
import Payment from "./component/Cart/Payment.js";
import { Elements } from "@stripe/react-stripe-js";
import { loadStripe } from "@stripe/stripe-js";
import ProtectedRoute from "./component/Route/ProtectedRoute.js";

function App() {

  const { isAuthenticated, user } = useSelector((state) => state.user);
const [stripeApiKey, setStripeApiKey]=useState("");


async function getStripeApiKey() {
  const { data } = await axios.get("/api/v1/stripeapikey");

  setStripeApiKey(data.stripeApiKey);
}


  React.useEffect(() => {
  WebFont.load({
    google:{
      families: ['Open Sans', 'Roboto'],
    },
  });


  store.dispatch(loadUser());
  getStripeApiKey();
  }, []);
  return (
    <BrowserRouter>
        <Header/>
        {isAuthenticated && <UserOptions user={user}/>}

<Elements stripe={loadStripe(stripeApiKey)}>
        <Routes>

              <Route exact path="/" element={<Home/>}/>
              <Route exact path="/product/:id" element={<ProductDetails/>}/>
              <Route exact path="/products" element={<Products/>}/>
              <Route path="/products/:keyword" element={<Products/>}/>
              <Route exact path="/search" element={<Search/>}/>

              <Route  exact path="/account" element={
                <ProtectedRoute><Profile/></ProtectedRoute>
              }/>
                         <Route path="/account" element={
                <ProtectedRoute isAuthenticated={isAuthenticated}><Profile/></ProtectedRoute>
              }/>
                  <Route path="/me/update" element={
                <ProtectedRoute isAuthenticated={isAuthenticated}><UpdateProfile/></ProtectedRoute>
              }/>
                  <Route path="/password/update" element={
                <ProtectedRoute isAuthenticated={isAuthenticated}><UpdatePassword/></ProtectedRoute>
              }/>


              <Route exact path="/password/forgot" element={<ForgotPassword/>}/>
              <Route exact path="/password/reset/:token" element={<ResetPassword/>}/>
              <Route exact path="/login" element={<LoginSignUp/>}/>
              <Route exact path="/cart" element={<Cart/>}/>

              <Route path="/shipping" element={
                <ProtectedRoute isAuthenticated={isAuthenticated}><UpdatePassword/></ProtectedRoute>
              }/>                  <Route path="/password/update" element={
                <ProtectedRoute isAuthenticated={isAuthenticated}><UpdatePassword/></ProtectedRoute>
              }/>                  <Route path="/password/update" element={
                <ProtectedRoute isAuthenticated={isAuthenticated}><UpdatePassword/></ProtectedRoute>
              }/>

              <Route exact path="/shipping" element={<Shipping/>}/>
              <Route exact path="/order/confirm" element={<ConfirmOrder/>}/>
              <Route exact path="/process/payment" element={<Payment/>}/>

              <Route path="password/reset/:token" element={
                <ProtectedRoute isAuthenticated={isAuthenticated}><ResetPassword/></ProtectedRoute>
              }/>



        </Routes>
        </Elements>

        <Footer/>
    </BrowserRouter>

  );
}

export default App;
